INSERT INTO `ncov_weibo_news` VALUES (7, '四川', '4464319981382765', 'http://weibo.com/2286908003/IqZfU5NIF', '【#四川启动一级响应#】1月24日，四川省委副书记、省长尹力主持召开省应对新型冠状病毒感染的肺炎疫情联防联控机制领导小组第三次全体会议，研判当前疫情发展态势，进一步加强突发公共卫生事件应急机制，完善防控策略和措施。会议建议，根据当前全省防控工作需要，依法依规启动突发公共卫生事件一级应急响应。@四川发布', '2020-01-24 18:04:49', '2286908003', '人民网', '2020-01-24 19:50:12');
