INSERT INTO `ncov_weibo_news` VALUES (37, '江西', '4464427363300029', 'http://weibo.com/2656274875/Ir236dQuh', '#最新疫情#【#江西新增确诊病例11例#】1月24日0-24时，江西省报告新型冠状病毒感染的肺炎新增确诊病例11例。其中抚州市3例、赣州市2例、上饶市2例、南昌市1例、九江市1例、景德镇市1例、宜春市1例。截至1月24日24时，江西省累计报告新型冠状病毒感染的肺炎确诊病例18例（含重症病例4例）。其中：景德镇市、赣州市、宜春市、上饶市为首次确诊病例。确诊病例中，抚州市4例、南昌市3例、九江市2例、赣州市2例、上饶市2例、景德镇市1例、萍乡市1例、新余市1例、宜春市1例、吉安市1例。<br>目前追踪到密切接触者484人，已解除医学观察19人，尚有465人正在接受医学观察。', '2020-01-25 01:11:31', '2656274875', '央视新闻', '2020-01-25 01:30:30');
INSERT INTO `ncov_weibo_news` VALUES (38, '广西', '4464426180723886', 'http://weibo.com/2656274875/Ir21c32jA', '#最新疫情#【#广西新增确诊10例# 累计23例】1月24日0-24时，广西报告新型冠状病毒感染的肺炎新增确诊病例10例。目前全区累计报告新型冠状病毒感染的肺炎确诊病例23例，其中柳州市3例、桂林市6例、梧州市1例、北海市6例、百色市1例、河池市3例、玉林市1例、防城港市2例。（总台央视记者张卉 尹智磊） ​​​', '2020-01-25 01:06:49', '2656274875', '央视新闻', '2020-01-25 01:30:31');
