INSERT INTO `ncov_weibo_news` VALUES (338, '武汉', '4464935650579970', 'http://weibo.com/2286908003/IrfgV2qSm', '#加油武汉#【正在直播：专家答疑如何预防新型肺炎】疫情紧急，如何预防新型冠状病毒肺炎？个人和家庭怎样做好防护工作？中国疾病预防控制中心消毒学首席专家张流波、研究员冯录召为你解答新型肺炎预防知识↓↓@人民日报  http://t.cn/A6PtLUNB ​​​', '2020-01-26 10:51:15', '2286908003', '人民网', '2020-01-26 10:55:06');
