INSERT INTO `ncov_weibo_news` VALUES (404, '武汉', '4465009302672851', 'http://weibo.com/1618051664/IrhbIbdkv', '【#武汉市累计确诊618例# 死亡45例】1月26日，国新办举行新型冠状病毒感染的肺炎疫情联防联控工作新闻发布会。国家卫健委副主任李斌介绍，我委接到湖北省卫生健康委报告，截至1月25日24时，湖北省武汉市累计报告618例确诊病例。其中，出院40例，现住院533例，死亡45例。现住院病例中有140例重症病例。湖北省武汉市报告现有112例疑似病例。（健康时报）关注新浪新闻，#疫情地图#实时更新中：http://t.cn/A6Pym3kM', '2020-01-26 15:43:56', '1618051664', '头条新闻', '2020-01-26 15:50:36');
