INSERT INTO `ncov_weibo_news` VALUES (412, '武汉', '4465038234952685', 'http://weibo.com/2286908003/IrhWnkMq1', '#疫情中的逆行者#【最美逆行医疗队，这支队伍叫“河南”！】26日下午，由137名医护人员组成的河南医疗队前往武汉，支援武汉抗击新型冠状病毒的肺炎疫情。临行前，护士王月华的丈夫对着大巴车，喊出了让全网泪奔的“我爱你”。致敬最美逆行者，愿你们平安归来！@人民网河南频道 http://t.cn/A6PcV6wS ​​​', '2020-01-26 17:38:53', '2286908003', '人民网', '2020-01-26 17:40:06');
