INSERT INTO `ncov_weibo_news` VALUES (437, '苏州', '4465071322863487', 'http://weibo.com/1618051664/IriNKc0Vh', '【#苏州推迟复工复业至元宵节#】苏州发布《关于加强新型冠状病毒感染的肺炎疫情防控工作的通告（第3号）》，其中包括：本行政区域内企业复工、复业时间不得早于2020年2月8日24时；学校开学时间不得早于2020年2月17日24时。涉及市民生活必需的行业（如供水、供气、供电、通讯、超市、农贸市场等）及疫情防控必需的行业除外。用人单位依法保障员工合法权益。（江苏新闻）', '2020-01-26 19:50:23', '1618051664', '头条新闻', '2020-01-26 19:50:44');
INSERT INTO `ncov_weibo_news` VALUES (438, '朝阳', '4465070375685656', 'http://weibo.com/1618051664/IriMdnR68', '【#北京新增9例新型肺炎确诊病例# 累计63例】北京市卫健委消息，1月26日8时至18时，北京新增9例新型肺炎病例。累计确诊病例63例，其中东城区1例、西城区5例、朝阳区11例、海淀区14例、丰台区4例、石景山区2例、通州区6例、顺义区1例、大兴区3例、昌平区5例，外地来京人员11例。（人民日报） ​​​', '2020-01-26 19:46:37', '1618051664', '头条新闻', '2020-01-26 19:50:44');
