INSERT INTO `ncov_weibo_news` VALUES (473, '上海', '4465141799893062', 'http://weibo.com/2656274875/IrkDpFvDw', '#急寻染病交通工具乘客#【紧急扩散！急寻#22日G1766次16车乘客#！】1月26日晚间23点12分，苏州市发布紧急通告：急寻2020年1月22日G1766次16车乘客！1月22日，由武汉出发G1766次列车至上海虹桥（苏州北站下车），16车发现1名新型冠状病毒感染的肺炎确诊病例。请同车旅客致电苏州市疾控中心应急值班电话18962168613，有专业人员为你指导！（总台央视记者吴睿）', '2020-01-27 00:30:26', '2656274875', '央视新闻', '2020-01-27 00:35:22');
