INSERT INTO `ncov_weibo_news` VALUES (605, '武汉', '4465352752768358', 'http://weibo.com/2656274875/Irq7FbCaW', '#疫情联防联控实时播报#【#澳大利亚确诊病例增至5例#】今天，澳大利亚新南威尔士州卫生部门确认了悉尼的一例新型冠状病毒感染的肺炎病例。最新的病例是一名21岁的中国女性，她上周从武汉飞抵悉尼，并在24小时内出现症状。至此，澳大利亚新型冠状病毒感染确诊病例上升到5例，其中新南威尔士州4例，维多利亚州1例。（总台央视记者林清辉）', '2020-01-27 14:28:41', '2656274875', '央视新闻', '2020-01-27 14:30:22');
