INSERT INTO `ncov_weibo_news` VALUES (651, '广州', '4465423032959033', 'http://weibo.com/2656274875/IrrX1cpMl', '#疫情联防联控实时播报#【#广东累计确诊病例151例#】今天0时至12时，广东省报告新型冠状病毒感染的肺炎新增确诊病例5例。其中广州市2例、梅州市2例、中山市1例。<br><br>截至今天12时，广东省累计报告新型冠状病毒感染的肺炎确诊病例151例。确诊病例中，广州41例、深圳36例、珠海12例、汕头2例、佛山14例、韶关4例、梅州3例、惠州8例、汕尾1例、东莞2例、中山5例、阳江8例、湛江5例、肇庆4例、清远3例、揭阳3例。<br><br>目前追踪到密切接触者804人，均在进行医学观察。', '2020-01-27 19:07:57', '2656274875', '央视新闻', '2020-01-27 19:10:21');
