INSERT INTO `ncov_weibo_news` VALUES (736, '武汉', '4465639509286467', 'http://weibo.com/1618051664/IrxAaCXPJ', '【#北京首例新冠病毒致死案例# 系原天合光能副总裁杨军】1月28日消息，21世纪经济报道记者获悉，北京首例因新冠病毒致死案例系原天合光能副总裁、中国商用价值群执行总裁杨军，年仅50岁。此前据官方通报，1月27日9时至20时，北京市新增8例新型冠状病毒感染的肺炎病例，其中1例为此前确诊病例的密切接触者，已送至北京市级定点医疗机构进行救治。死亡1例，男，50岁，1月8日前往武汉，15日返京后出现发热症状，21日到医院就诊，22日确诊，今日病情恶化，因呼吸功能衰竭，经抢救无效死亡。（21世纪经济报）', '2020-01-28 09:28:09', '1618051664', '头条新闻', '2020-01-28 09:30:38');
