INSERT INTO `ncov_weibo_news` VALUES (1068, '湖北', '4466363555253374', 'http://weibo.com/1618051664/IrQpZm2DQ', '【湖北公安：#感染者向他人吐口水将追刑责#[话筒]】29日，湖北省公安厅发布《关于依法严厉打击涉医违法犯罪维护医疗秩序的通告》明确，新型冠状病毒携带者在公共场合向他人吐口水、患有或疑似患有新型冠状病毒感染的肺炎拒绝接受检疫、强制隔离或治疗，过失造成传染病传播的，将被追究刑事责任。 ​​​​（中国新闻网）', '2020-01-30 09:25:15', '1618051664', '头条新闻', '2020-01-30 09:25:36');
