INSERT INTO `ncov_weibo_news` VALUES (1319, '海南', '4467136301294623', 'http://weibo.com/2656274875/Isawm5qN1', '#共同战疫#【#海南2月24日前不得开学#】海南省新型冠状病毒感染肺炎疫情防控工作指挥部决定：海南省各级各类学校（含高校、中职学校、中小学、幼儿园等）于2月24日前不得开学。为有效应对“返工潮”对海南防疫工作的影响，海南省企业可灵活安排工作。从岛外返琼人员一律实行14天隔离观察，有症状的一律集中观察。（总台央视记者毛鑫 杨涛 叶飞）', '2020-02-01 12:35:52', '2656274875', '央视新闻', '2020-02-01 12:40:21');
