INSERT INTO `ncov_weibo_news` VALUES (1321, '湖南', '4467141690502373', 'http://weibo.com/1618051664/IsaF326GN', '【湖南官员突发心梗去世 长期坚守防控一线】湖南省卫生计生综合监督局党委书记、局长张辉同志因劳累过度，突发心梗抢救无效，于2月1日凌晨不幸逝世，终年56岁。新型冠状病毒感染的肺炎疫情发生以来，张辉一直坚守在防控一线，据@湖南都市-都市晚间 ，从1月17号起，张辉局长已经在工作岗位连轴工作了15整天。（湖南日报）', '2020-02-01 12:57:17', '1618051664', '头条新闻', '2020-02-01 13:00:38');
