INSERT INTO `ncov_weibo_news` VALUES (1519, '海南', '4467816893662201', 'http://weibo.com/2286908003/Isse5fmHL', '#万众一心抗击疫情#【图解：居家医学观察要注意这九条】居家医学观察究竟从什么时间算起？如何居家医学观察？海南省卫健委副主任李文秀表示，新型冠状病毒感染的肺炎确诊病例的密切接触者应从和病人接触的最后一天起采取医学观察14天。在家中观察期间需与医学观察人员保持联系，并需要了解病情观察和护理要点，掌握家庭预防的洗手、通风、防护和消毒措施。@人民网海南频道 http://t.cn/A6PWeggQ', '2020-02-03 09:40:18', '2286908003', '人民网', '2020-02-03 09:45:06');
