INSERT INTO `ncov_weibo_news` VALUES (1965, '湖北', '4470484508849139', 'http://weibo.com/2286908003/ItzCGB843', '【#湖北一天排除疑似病例七千余人# 速度大幅加快！】#新冠肺炎最新动态#，10日，湖北省卫健委通报，2月9日0时至24时，全省新增新型冠状病毒感染的肺炎病例2618例、死亡91例、出院356例。全省累计报告确诊病例29631例、已治愈出院1795例、死亡871例。相比8日排除疑似病例1128人，9日排除7194人，排除速度大幅加快。 ​​​​http://t.cn/A6hP26aH', '2020-02-10 18:20:26', '2286908003', '人民网', '2020-02-10 18:25:06');
INSERT INTO `ncov_weibo_news` VALUES (1966, '武汉', '4470485725200361', 'http://weibo.com/2656274875/ItzEElOQN', '#习近平调研指导新冠肺炎疫情防控#【习近平考察北京地坛医院和朝阳区疾控中心】10日下午，习近平来到北京地坛医院，察看新冠肺炎感染者住院诊疗情况，视频连线武汉市重症患者收治医院，听取中央指导组、湖北指挥部有关情况汇报，慰问奋战在一线的医务工作者。随后，习近平前往朝阳区疾病预防控制中心，了解朝阳区开展应急处置、流行病学调查、筛查报告和北京市疫情防控工作情况。', '2020-02-10 18:25:16', '2656274875', '央视新闻', '2020-02-10 18:30:21');
