INSERT INTO `ncov_weibo_news` VALUES (2021, '河南', '4471463434306967', 'http://weibo.com/2656274875/ItZ5Bi4rd', '【#河南近期严禁师生返校# 开学时间待定】据河南省教育厅新冠肺炎疫情防控工作领导小组关于近期严禁师生返校的紧急通知：各地、各级各类学校在接到河南省疫情防控指挥部教育系统工作专班批准开学前，不得安排学生返校。除需要返校参加本校疫情防控工作的教职工外，不得安排其他教职工返校。各级各类学校开学时间，以河南省疫情防控指挥部教育系统工作专班正式批准的方案为准。（总台央视记者田萌）', '2020-02-13 11:10:21', '2656274875', '央视新闻', '2020-02-13 11:15:20');
