INSERT INTO `ncov_weibo_news` VALUES (2223, '吉林', '4477967256737372', 'http://weibo.com/2286908003/IwIhDsgHi', '【#25省区市新增病例为0#】#新冠肺炎最新动态# 3月1日0-24时，天津、河北、山西、辽宁、吉林、黑龙江、上海、江苏、安徽、福建、江西、河南、湖南、广西、海南、重庆、四川、贵州、云南、西藏、青海、新疆（含兵团）新增确诊病例为0。3月1日8时至2日8时，陕西无新增新冠肺炎确诊病例；2月29日20时至3月1日20时，甘肃省无新增新型冠状病毒肺炎确诊病例；3月1日7时至2日7时，内蒙古自治区报告无新增新冠肺炎确诊病例。', '2020-03-02 09:54:13', '2286908003', '人民网', '2020-03-02 09:55:08');
