INSERT INTO `ncov_weibo_news` VALUES (2263, '武汉', '4480192363576243', 'http://weibo.com/2286908003/IxEawf0ll', '【武汉日记：“你是天使，也是战士”】核酸检测是确诊和排查新冠肺炎的重要环节。2月7日，武汉武昌区疾控中心组建隔离点#核酸检测采样小分队2/3都是女生#。面对镜头，姑娘们说出了待疫情结束后的心愿：有的要去看奶奶、有的要穿高跟鞋、有的是脱单…#AB面挑战#天使们节日快乐~ http://t.cn/A67kyjYO ​​​', '2020-03-08 13:15:59', '2286908003', '人民网', '2020-03-08 13:35:09');
