INSERT INTO `ncov_weibo_news` VALUES (2393, '广东', '4487456873653178', 'http://weibo.com/2286908003/IAH9tfkme', '【#中国特色诊疗方案了解一下#】全球战疫进行时，中医专家谈疫情。中国特色诊疗方案你了解多少？“中医”在抗疫中又发挥了怎样重要的价值？《新型冠状病毒肺炎防治诊疗方案》起草组副组长、第二批国家援助湖北中医医疗队队长、广东省中医院副院长张忠德教授为你详解诊疗方案中医药配方。（人民健康） http://t.cn/A6Zq9awB', '2020-03-28 14:22:34', '2286908003', '人民网', '2020-03-28 14:25:07');
